import React from "react";
import lightBaseTheme from "material-ui/styles/baseThemes/lightBaseTheme";
import MuiThemeProvider from "material-ui/styles/MuiThemeProvider";
import getMuiTheme from "material-ui/styles/getMuiTheme";
import Paper from "material-ui/Paper";
import { withRouter } from "react-router-dom";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";
import ListSubheader from "@material-ui/core/ListSubheader";
import Button from "@material-ui/core/Button";
import Avatar from "@material-ui/core/Avatar";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import StepContent from "@material-ui/core/StepContent";
import Slider from '@material-ui/lab/Slider';
import Typography from '@material-ui/core/Typography';
import MenuBar from "./MenuBar";
import axios from "axios"

class Loan extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      expanded: false,
      tileData: [],
      auth: true,
      anchorEl: null,
      open: false,
      activeStep: 0,
      borrow: 5,
      duration: 10,
      limit: 25,
      apr: 7,
      monthly: 8000,
    };
  }
  handleChange1 = (event,value) => {
    this.setState({ borrow: value });
    //console.log("slide",value)
  };
  handleChange2 = (event,value) => {
    this.setState({ duration: value });
    //console.log("slide",value)
  };
  componentDidMount = () => {

  };
  handleVerify = () => {
    this.setState({activeStep: 1})
  };
  handleKiva = () => {
    this.setState({activeStep: 2})
  };
  reset = () => {
    this.setState({activeStep: 0})
  };

  render() {
    return (
      <loan>
        <MuiThemeProvider muiTheme={getMuiTheme(lightBaseTheme)}>
          <div>
            <MenuBar />
            <Paper style={{ width: 450 }}>
            <Stepper
              activeStep={this.state.activeStep}
              orientation="vertical"
            >
              <Step key="step1">
                <StepLabel>Input Loan Details</StepLabel>
                <StepContent>
                <Typography id="label">Borrow Amount: ${this.state.borrow*1000}</Typography>
                <br/>
                <Slider
                  style={{width: 250}}
                  value={this.state.borrow}
                  aria-labelledby="label"
                  onChange={this.handleChange1}
                />
                <br/>
                <Typography id="label">Repayment duration: ${this.state.duration*3}</Typography>
                <br/>
                <Slider
                  style={{width: 250}}
                  value={this.state.duration}
                  aria-labelledby="label"
                  onChange={this.handleChange2}
                />
                <br/>
                  <Button
                    variant="outlined"
                    color="primary"
                    onClick={this.handleVerify}
                  >
                    Submit
                  </Button>
                </StepContent>
              </Step>
              <Step key="step2">
                <StepLabel>Prelimiary Loan Suggestion</StepLabel>
                <StepContent>
                <Typography id="label">Estimated Amount can be borrowed from Bank: ${this.state.borrow>this.state.limit ? this.state.limit*1000:this.state.borrow*1000}</Typography>
                <Typography id="label">Remaining Amount can be borrowed from Kiva: ${this.state.borrow>this.state.limit ? Math.round(this.state.borrow-this.state.limit)*1000:0}</Typography>
                <br/>
                  <Button
                    variant="outlined"
                    color="primary"
                    onClick={this.handleKiva}
                  >
                    Apply
                  </Button>
                </StepContent>
              </Step>
              <Step key="step3">
                <StepLabel>Borrow from Kiva</StepLabel>
                <StepContent>
                  <Typography id="label">Annual Interest: ${this.state.apr}</Typography><br/>
                  <Typography id="label">Monthly Payment: ${this.state.monthly}</Typography><br/>
                  <br/><br/><a href="https://www.business.hsbc.uk/en-gb/finance-and-borrowing/credit-and-lending/small-business-loan">Apply HSBC loan</a>
                  <br/><br/><a href="https://pages.kiva.org/borrow/start">Apply Kiva loan</a>
                  <br/><br/>
                  <Button
                    variant="outlined"
                    color="primary"
                    onClick={this.reset}
                  >
                    Start Over
                  </Button>
                </StepContent>
              </Step>
            </Stepper>
            </Paper>
          </div>
        </MuiThemeProvider>
      </loan>
    );
  }
}
const LoanWithRouter = withRouter(Loan);
export default LoanWithRouter;
